import React from 'react';
// import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import './CSS/CSS_FILE.css';
import About from './About.js';
import Experience from './Experience.js';
import Skills from './Skills.js';
import Header from './Header.js';
import './index.js';
import Contact from './Contact.js';

function App() {
  const scrollOffset = 300;
  const contentElementsRef = useRef([]);

  // Simulating document.querySelectorAll("[id^='content']") in React
  useEffect(() => {
    contentElementsRef.current = Array.from(document.querySelectorAll("[id^='content']"));
  }, []);

  const elementInView = (el, offset = 0) => {
    const elementTop = el.getBoundingClientRect().top;
    return elementTop <= (window.innerHeight - offset);
  };

  const displayScrollElement = (el) => {
    el.classList.add('scrolled');
  };

  const hideScrollElement = (el) => {
    el.classList.remove('scrolled');
  };

  const handleScrollAnimation = () => {
    contentElementsRef.current.forEach((element) => {
      if (elementInView(element, scrollOffset)) {
        displayScrollElement(element);
      } else {
        hideScrollElement(element);
      }
    });
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScrollAnimation);
    return () => {
      window.removeEventListener('scroll', handleScrollAnimation);
    };
  }, []);
  return (
    <div>
        <Header/>
        <About/>
        <Experience/>
        <Skills/>
        <Contact/>
    </div>
  );
}

export default App;
